package Loop;

public class DoWhileLoopExmaple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		 int i = 0;      
	        System.out.println("Printing the list of first 10 even numbers ");      
	        do {      
	            System.out.println(i);      
	            i = i + 2;      
	        }while(i<=10);      
		

	}

}
