package Operators;

public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 5;
		int c = 7;
		
		System.out.println("a>c : " + (a>c));
		System.out.println("a<c : " + (a<c));
		System.out.println("a>=b : " + (a>=b));
		System.out.println("a<=b: " + (a<=b));
		System.out.println("a==c : " +(a==b));
		System.out.println("a!=b :" + (a!=b));
		

	}

}
