package conditional;

public class IfElseIfLadderStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int number = 0;

        if (number > 0) {
            System.out.println("Positive number.");
        } else if (number < 0) {
            System.out.println("Negative number.");
        } else {
            System.out.println("Zero.");

	}

}
}